import React, { Component } from'react';
import { Card, CardHeader, CardBody,
  CardTitle, CardText } from 'reactstrap';

import Child from './ItemList.js'

class Notificaciones extends React.Component{
  constructor(){
     super();
     this.state = {
       items: [
        "Lorem ipsum dolor amet, consectetur adipiscing elit.",
        "Nullam rutrum felis ut metus tempus porttitor.",
        "Etiam ipsum risus, faucibus ut iaculis eget, pretium risus.",
        "Duis arcurisus vehicula consectetur ac non enim.",
        "Duis non convallis odio. Quisque maximus eu lorem eu laoreet."]
     }
     this.delete = this.delete.bind(this);
  }

  delete(id){
     this.setState(prevState => ({
         data: prevState.data.filter(el => el != id )
     }));
  }

  render(){
     return(
      <div>
        <Card>
         <CardHeader>
            <h5>Notificaciones</h5>
         </CardHeader>
         <CardBody>
            <CardTitle>
              <Child items={this.state.items} _handleDelete={this.delete.bind(this)}/>

              {/* <Child delete={this.delete} data={this.state.data}/> */}
          </CardTitle>
         </CardBody>
        </Card>
     </div>
     );
  }
}

export default Notificaciones;
